sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	//ahora guarda la hora actual
	var rightnow  = new GlideDateTime();
	//se crea un objeto para almacenar "cuando se use"
	var whenNeeded = new GlideDateTime(current.u_when_needed);

	//si la hora de whenNeeded es anterior a la de rightnow se cancela la operacion y saca un mensaje de error

	if(whenNeeded.before(rightnow)){
		gs.addErrorMessage("No puedes guardar cosas en el pasado de la fecha actual. Tu peticion a sido rechazada.");
		current.setAbortAction(true);

	}

	//Ejercicio
	// Toma las variables  y las ejecuta para ser ocupadas en otra operacion 
	var today = rightnow.getLocalDate();
	var todayIs = whenNeeded.getLocalDate();

	//comparamos today y todayIs para ver si son iguales

	if(today.compareTo(todayIs)==0){
		gs.addErrorMessage("no puedes agregar este pedo we , danos chance al menos de un dia");


	}




})(current, previous);
